//GetMainUrl คืนค่า url หลัก
func GetMainURL() string {
	return "http://reg.buu.ac.th/registrar/"
}

//GetURL สร้างurl สำหรับ ปี เทอมปัจจุบัน
func GetURL(year int, semester int) string {
	URL := "http://reg.buu.ac.th/registrar/class_info_1.asp?coursestatus=O00&facultyid=003%A4%B3%D0%C7%D4%B7%C2%D2%C8%D2%CA%B5%C3%EC&maxrow=$maxrow&acadyear=$year&semester=$semester&CAMPUSID=1&LEVELID=&coursecode=*&coursename=&cmd=2"
	URL = strings.Replace(URL, "$year", strconv.Itoa(GetYear()), 1)
	URL = strings.Replace(URL, "$semester", strconv.Itoa(GetSemester()), 1)
	URL = strings.Replace(URL, "$maxrow", strconv.Itoa(GetMaxRow()), 1)
	return URL
}
